mapper.select:
		SELECT * FROM (
    		SELECT ROWNUM rnum, tb.* FROM (
        		SELECT hotelCode, hname, locName, name
        		FROM hotelInfo h
        		JOIN locationCode3 l ON h.loccode=l.loccode
        		
        			
						
					 
        		
        		ORDER BY hotelCode DESC
     
    		)tb WHERE rownum <= #{end}
		) where rnum >= #{start}
