mapper.select:
		SELECT * FROM (
	    	SELECT ROWNUM rnum, tb.* FROM (
	        	SELECT q.QNACODE, q.CATCODE, subject, content, TO_CHAR(q.created, 'YYYY-MM-DD') created, q.userIdx,
	                userName, a.adminIdx, ad.adminName, hitCount, answercode, answerCreated, answerContent, 
	                NVL(answerCount, 0) answerCount
	        	FROM question q
	        	LEFT OUTER JOIN answer a ON q.qnacode = a.qnacode
	        	JOIN tb_user u ON q.userIdx = u.userIdx
                LEFT OUTER JOIN admin ad ON ad.adminIdx = a.adminIdx
	            LEFT OUTER JOIN (
	                SELECT qe.qnaCode, COUNT(*) answerCount
	                FROM question qe
	                JOIN answer an
	                ON qe.qnaCode = an.qnaCode
	                GROUP BY qe.qnaCode
	            ) qe ON qe.qnaCode = q.qnaCode
	            
					
					
					
					AND CATCODE = #{catCode}
				
	        	ORDER BY created DESC  	
		
	    	) tb WHERE ROWNUM <= #{end}
		) WHERE rnum >= #{start}
